t.script:
function pageRedirect() {
    var is_debug = location.search;
    if ( is_debug == '?debug') {
        url = window.location.origin +"/web?debug";
        window.location.href = url;
    } else {
        url = window.location.origin +"/web";
        window.location.href = url;
    }
}
pageRedirect();
